import React from "react";

let data = require("../src/data.json");

const names = data.map(name => {
  return [new Set(name.name)];
});

class App extends React.Component {
  state = {
    name: ""
  };

  render() {
    return (
      <div className="App">
        <label>Customer Name</label>
        <input type="text" name="name" list="names" />
        <datalist id="names">
          {names.map(name => (
            <option>{name}</option>
          ))}
        </datalist>
      </div>
    );
  }
}

export default App;
