import React from 'react';


const names = ["mike","larry","todd"]

class App extends React.Component {

  render(){
    return (



    <div className="App">
      <label>Customer Name</label>
        <input type="text" name="name" list="names"/>
          <datalist id="names">
            {names.map(name => <option>{name}</option>)}
          </datalist>
    </div>
  );
  }
}

export default App;
